}

# Load saved servers
load_servers() {
    if [[ -f ".deploy-servers" ]]; then
        source .deploy-servers
    fi
}

# Save server configuration
save_server() {
    local name="$1"
    local host="$2"
    local user="$3"
    local port="$4"
    
    echo "SERVER_${name^^}_HOST=\"$host\"" >> .deploy-servers
    echo "SERVER_${name^^}_USER=\"$user\"" >> .deploy-servers
    echo "SERVER_${name^^}_PORT=\"$port\"" >> .deploy-servers
    
    success "Server '$name' saved"
}

# List saved servers
list_servers() {
    if [[ ! -f ".deploy-servers" ]]; then
        info "No saved servers found"
        return
    fi
    
