    load_servers
    
    # Extract existing server configurations from the .deploy-servers file
    if [[ -f ".deploy-servers" ]]; then
        local servers=$(grep "SERVER_.*_HOST=" .deploy-servers | sed 's/SERVER_//g' | sed 's/_HOST=.*//g' | tr '[:upper:]' '[:lower:]' | sort -u)
        
        if [[ -n "$servers" ]]; then
            printf "%-10s %-25s %-15s %-6s\n" "Name" "Host" "User" "Port"
            echo "━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━"
            
            for server in $servers; do
                local host_var="SERVER_${server^^}_HOST"
                local user_var="SERVER_${server^^}_USER"
                local port_var="SERVER_${server^^}_PORT"
                
                local host="${!host_var:-}"
                local user="${!user_var:-root}"
                local port="${!port_var:-22}"
                
                if [[ -n "$host" ]]; then
                    printf "%-10s %-25s %-15s %-6s\n" "$server" "$host" "$user" "$port"
                fi
            done
        fi
    fi
}

# Quick deploy to saved server
quick_deploy() {
    local server_name="$1"
    local mode="${2:-quick}"
    
    load_servers
    
    local host_var="SERVER_${server_name^^}_HOST"
    local user_var="SERVER_${server_name^^}_USER"
    local port_var="SERVER_${server_name^^}_PORT"
    
    local host="${!host_var:-}"
    local user="${!user_var:-root}"
    local port="${!port_var:-22}"
    
    if [[ -z "$host" ]]; then
        error "Server '$server_name' not found. Use 'list' to see available servers."
    fi
    
    log "🚀 Deploying to server: $server_name ($user@$host:$port)"
    
    local deploy_args="--host $host --user $user --port $port"
    
    case "$mode" in
        "clean") deploy_args="$deploy_args --clean" ;;
        "rebuild") deploy_args="$deploy_args --force-rebuild" ;;
        "setup") deploy_args="$deploy_args --setup-only" ;;
        "deploy") deploy_args="$deploy_args --deploy-only" ;;
    esac
    
    ./universal-deployer.sh $deploy_args
}

# Add new server
add_server() {
    echo -e "${CYAN}➕ Add New Server${NC}"
    echo "━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━━"
    
    read -p "Server name (e.g. prod, staging, dev): " name
    read -p "Host (IP or domain): " host
    read -p "SSH user [default: root]: " user
    read -p "SSH port [default: 22]: " port
    
    user="${user:-root}"
    port="${port:-22}"
    
    # Validate input
    if [[ -z "$name" || -z "$host" ]]; then
        error "Name and host are required"
    fi
    
    # Test connection
    log "🔐 Testing SSH connection..."
    if ssh -p "$port" -o ConnectTimeout=10 -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null -o BatchMode=yes "$user@$host" "echo 'SSH_TEST_OK'" 2>/dev/null | grep -q "SSH_TEST_OK"; then
        success "SSH connection verified"
        save_server "$name" "$host" "$user" "$port"
    else
        warning "SSH connection failed. Server saved but might not be accessible."
        save_server "$name" "$host" "$user" "$port"
    fi
}

# Remove server
remove_server() {
    local server_name="$1"
    
    if [[ -z "$server_name" ]]; then
        read -p "Enter server name to remove: " server_name
    fi
    
    if [[ -f ".deploy-servers" ]]; then
        # Remove server entries
        sed -i "/SERVER_${server_name^^}_/d" .deploy-servers
        success "Server '$server_name' removed"
    else
        warning "No servers file found"
    fi
}

# Show usage
show_usage() {
    echo "Usage: $0 [COMMAND] [OPTIONS]"
    echo ""
    echo "Commands:"
    echo "  list                    List saved servers"
    echo "  add                     Add new server interactively"
    echo "  remove <name>           Remove saved server"
    echo "  deploy <name> [mode]    Deploy to saved server"
    echo "  status <name>           Show server status"
    echo "  logs <name>             Show deployment logs"
    echo "  ssh <name>              SSH to server"
    echo ""
    echo "Deploy modes:"
    echo "  quick     - Quick deploy (default)"
    echo "  clean     - Clean deploy (remove containers)"
    echo "  rebuild   - Force rebuild images"
    echo "  setup     - Setup server only"
    echo "  deploy    - Deploy app only"
    echo ""
    echo "Examples:"
    echo "  $0 list"
    echo "  $0 add"
    echo "  $0 deploy prod"
    echo "  $0 deploy staging clean"
    echo "  $0 status prod"
    echo "  $0 ssh prod"
    echo ""
}

# SSH to server
ssh_to_server() {
    local server_name="$1"
    
    load_servers
    
    local host_var="SERVER_${server_name^^}_HOST"
    local user_var="SERVER_${server_name^^}_USER"
    local port_var="SERVER_${server_name^^}_PORT"
    
    local host="${!host_var:-}"
    local user="${!user_var:-root}"
    local port="${!port_var:-22}"
    
    if [[ -z "$host" ]]; then
        error "Server '$server_name' not found"
    fi
    
    log "🔐 Connecting to $server_name ($user@$host:$port)..."
    ssh -p "$port" "$user@$host"
}

# Show server status
show_server_status() {
    local server_name="$1"
    
    load_servers
    
    local host_var="SERVER_${server_name^^}_HOST"
    local user_var="SERVER_${server_name^^}_USER"
    local port_var="SERVER_${server_name^^}_PORT"
    
    local host="${!host_var:-}"
    local user="${!user_var:-root}"
    local port="${!port_var:-22}"
    
    if [[ -z "$host" ]]; then
        error "Server '$server_name' not found"
    fi
    
    ./universal-deployer.sh --host "$host" --user "$user" --port "$port" --status
}

# Show deployment logs
show_logs() {
    local server_name="$1"
    
    load_servers
    
    local host_var="SERVER_${server_name^^}_HOST"
    local user_var="SERVER_${server_name^^}_USER"
    local port_var="SERVER_${server_name^^}_PORT"
    
    local host="${!host_var:-}"
    local user="${!user_var:-root}"
    local port="${!port_var:-22}"
    
    if [[ -z "$host" ]]; then
        error "Server '$server_name' not found"
    fi
    
    log "📋 Fetching logs from $server_name..."
    ssh -p "$port" "$user@$host" "cd /opt/katacore && docker compose logs -f --tail=100"
}

# Main execution
case "${1:-}" in
    "list"|"ls")
        show_banner
        list_servers
        ;;
    "add"|"new")
        show_banner
        add_server
        ;;
    "remove"|"rm"|"delete")
        remove_server "$2"
        ;;
    "deploy"|"d")
        if [[ -z "$2" ]]; then
            error "Server name required. Use: $0 deploy <server_name> [mode]"
        fi
        quick_deploy "$2" "${3:-quick}"
        ;;
    "status"|"s")
        if [[ -z "$2" ]]; then
            error "Server name required. Use: $0 status <server_name>"
        fi
        show_server_status "$2"
        ;;
    "logs"|"log"|"l")
        if [[ -z "$2" ]]; then
            error "Server name required. Use: $0 logs <server_name>"
        fi
        show_logs "$2"
        ;;
    "ssh")
        if [[ -z "$2" ]]; then
            error "Server name required. Use: $0 ssh <server_name>"
        fi
        ssh_to_server "$2"
        ;;
    "help"|"--help"|"-h"|"")
        show_banner
        show_usage
        ;;
    *)
        error "Unknown command: $1. Use '$0 help' for usage information."
        ;;
esac
